nested-code:
body {
  margin: 0;
  padding: 0;
  h1, h2, h3 {
    color: var(--primary-color);
  }
}

layers-code:
@layer base {
  body {
    background: var(--primary-color);
  }
}
@layer components {
  button {
    background: var(--secondary-color);
  }
}

